def maj2
    print "-Combien contiennent une majuscule ?  "
    i = 0
    @journalistes.each do |b|
        if b =~ /[A-Z]/     
        i += 1
        end
    end
    puts " Il y en a #{i} qui contiennent des majuscules. " 
    end
    maj2
    # =~ --> match (correspondance d'expression) 
    # // --> insertion d'un pattern  
    # [A-Z] --> inclus toutes les majuscules de A à Z

